import React, {Component} from 'react';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';
import {fetchCountryData} from './api'
import axios from 'axios';

const mapStyles = {
  width: '80%',
  height: '70%',
};

async function mapEffect({ leafletElement: map } = {}) {
  let response;

  try {
    response = await axios.get('https://api.covid19api.com/summary');
  } catch(e) {
    console.log(`Failed to fetch countries: ${e.message}`, e);
    return;
  }

  const { data = [] } = response;

  const hasData = Array.isArray(data) && data.length > 0;
  if ( !hasData ) return;
  const geoJson = {
    type: 'FeatureCollection',
    features: data.map((country = {}) => {
      const { countryInfo = {} } = country;
      const { lat, long: lng } = countryInfo;
      return {
        type: 'Feature',
        properties: {
          ...country,
        },
        geometry: {
          type: 'Point',
          coordinates: [ lng, lat ]
        }
      }
    })
  }
}

export class MapContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      stores: [{lat: 39.8283, lng: -98.5795},
              {latitude: 56.1304, longitude: -106.3468},
              {latitude: 39.8283, longitude: -98.5795},
              {latitude: 33.93911, longitude: 67.709953},
              {latitude: 61.5240, longitude: 105.3188}]
    }
  }

  displayMarkers = () => {
    return this.state.stores.map((store, index) => {
      return <Marker 
        key={index} 
        id={index} 
        position={{
          lat: store.latitude,
          lng: store.longitude
     }}
     onClick={() => console.log("You clicked me!")} />
    })
  }

  render() {
    return (
        <Map
          google={this.props.google}
          zoom={1}
          style={mapStyles}
          initialCenter={{ lat: 39.8283, lng: -98.5795}}
        >
          {this.displayMarkers()}
          {/* <Marker
            lat={39.8283}
            lng={-98.5795}
            name="My Marker"
            color="red"
          /> */}
        </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: 'AIzaSyCW_Vioa3QR3TUnN6MuFdprCqabI055FZk'
})(MapContainer);
