import React, { Component } from 'react'
import { connect } from 'react-redux'
import ReportList from '../CaseData/ReportList' //Report list displays table with report data

class Report extends Component {

    state = {
        name: '',
        symptom
    }
    handleChange =(e) => {
        this.setState({
            [e.target.id]: e.target 
         })
    }
    handleSubmit =(e) => {
        console.log(e)
    }
    render () {
        console.log(this.props);
        return (
            <div className="report container">
                <h4 className="center">Report Symptom</h4>
                <form onSubmit={ this.handleSubmit }>
                    <label>
                        Enter your name:
                        <input type="text" id="name" name ="name" />
                        Do you have a symptom?
                        <input type="text" id="symptom" name="symptom" />
                        Enter your city:
                        <input type="text" id="city" name="city" />
                        Enter zipcode:
                        <input type="number" id="zip" name="zip" />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            <ReportList/>  
            </div> 
        )
    }
}

const mapStateToProps = (state) => {
    return{
        reports: state.report.reports
    }
}

export default connect(mapStateToProps)(Report)
